#!/bin/bash
exec /usr/bin/java -Xmx26g -XX:GCTimeLimit=60 -XX:+UseConcMarkSweepGC -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=/deviceanalyzer/export/crunch-heapdumps -Duk.ac.cam.deviceanalyzer.datadir=/deviceanalyzer/export/data -Duk.ac.cam.deviceanalyzer.workingdir=/deviceanalyzer/export/working -Duk.ac.cam.deviceanalyzer.archivedir=/deviceanalyzer/data/archive -Duk.ac.cam.deviceanalyzer.brokenarchivedir=/deviceanalyzer/data/archive-suspected-broken/ -Duk.ac.cam.deviceanalyzer.uploaddir=/deviceanalyzer/data/uploads/ -Duk.ac.cam.deviceanalyzer.crashdir=/deviceanalyzer/data/crashes/ -Duk.ac.cam.deviceanalyzer.analysisdir=/deviceanalyzer/export/analysed/ -Duk.ac.cam.deviceanalyzer.analyticsdir=/deviceanalyzer/export/analytics/ -Duk.ac.cam.deviceanalyzer.exportOnly=false -Duk.ac.cam.deviceanalyzer.production=true -jar /usr/local/distanalysis/DistAnalysis.jar >/tmp/distanalysis.log 2>&1
