#!/bin/bash
exec /usr/bin/java -Xmx26g -XX:GCTimeLimit=60 -XX:+UseConcMarkSweepGC -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=/deviceanalyzer/analysis/crunch-heapdumps -Duk.ac.cam.deviceanalyzer.archivedir=/deviceanalyzer/archive/archive -Duk.ac.cam.deviceanalyzer.brokenarchivedir=/deviceanalyzer/archive/archive-suspected-broken/ -Duk.ac.cam.deviceanalyzer.analysisdir=/deviceanalyzer/analysis/analysed/  -Duk.ac.cam.deviceanalyzer.exportOnly=false -Duk.ac.cam.deviceanalyzer.production=true -jar /usr/local/distanalysis/DistAnalysis.jar >/tmp/distanalysis.log 2>&1
